import React, {useState}from 'react';
import Avatar from '@material-ui/core/Avatar/Avatar'
import "./ChatScreen.css";

function ChatScreen() {
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState([
        {
            name: '수지',
            message: '아 과제 너무 많아 ㅠㅠ',
            profile: 'data:image/jpeg;base64,'
        },
        {
            name: '수지',
            message: '그래도 우리 조만간 한번 봐야지~ 본지 너무 오래됐어!',
            profile: 'data:image/jpeg;base64,'
        },
        {
            message: '응 언제 볼까?',
        },
        {
            name: '수지',
            message: '오늘 뭐해?',
            profile: 'data:image/jpeg;base64,'
        }
    ])
    
    const onSendButton = (event) => {
        event.preventDefault();
        if(input) {
            setMessages([...messages, {
                message: input,
            }
            ]);
            setInput('');
        }
    }

    const onChangeInput = (event) => {
        setInput(event.target.value);
    }

    return (
        <div className="Screen">
            <div className="chatScreen">
                <p>You matched with {messages[0].name} !!</p>
                {messages.map(message => (
                    message.name ? (
                    <div className="chatScreen__detail">
                        <Avatar className="chatScreen__image" alt={message.name} src={message.profile}/>
                        <p className="chatScreen__text">{message.message}</p>
                    </div>
                    ) : (
                        <div className="chatScreen__detail Selfchat">
                            <p>{message.message}</p>
                        </div>
                    )
                ))}
            </div>

            <form className="chatScreen__form">
                <input type="text"
                 value={input}
                 className="chatScreen__input"
                 placeholder="메세지를 입력하세요.." 
                 onChange={onChangeInput}
                 />
                <button className="chatScreen__button"
                    onClick={onSendButton}
                     type="submit"
                     >전송</button>
            </form>
        </div>
    )
}

export default ChatScreen;
